-- ----------------------------
-- Table structure for `article_base`
-- ----------------------------
DROP TABLE IF EXISTS `article_base`;
CREATE TABLE `article_base` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `version` bigint(20) DEFAULT NULL COMMENT '版本',
  `create_time` bigint(20) NOT NULL COMMENT '添加时间',
  `creater` varchar(32) NOT NULL COMMENT '添加人',
  `modifier` varchar(32) NOT NULL COMMENT '修改人',
  `modify_time` bigint(20) NOT NULL COMMENT '修改时间',
  `article_sort` bigint(20) DEFAULT NULL COMMENT '文章排序',
  `brief` varchar(255) DEFAULT NULL COMMENT '缩略',
  `category_id` bigint(20) DEFAULT NULL COMMENT '分类id',
  `content` longtext COMMENT '内容',
  `pic` varchar(255) DEFAULT NULL COMMENT '图片',
  `source` varchar(255) DEFAULT NULL COMMENT '来源',
  `tag` varchar(255) DEFAULT NULL COMMENT '标签',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `user_name` varchar(255) DEFAULT NULL COMMENT '用户名',
  `description` varchar(200) DEFAULT NULL COMMENT '描述',
  `keywords` varchar(200) DEFAULT NULL COMMENT '关键词',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;

