-- Records of article_base
-- ----------------------------
INSERT INTO `article_base` VALUES ('11', '0', '1444701181684', 'yy.tanhualong', 'yy.tanhualong', '1444701181684', null, '由香港新展国际展览有限公司联合广州瑞鸿展览服务有限公司联手主办的2015中国（广州）国际玻璃工业技术展览会将于9月23-25日在广州琶洲保利展馆举行，本次展会作为华南年度唯一一个最专业的玻璃工业展览会，华南玻璃行业重要的展览展示交易平台，将为技术领先的国际化公司和专业化为特色的中小企业带来更多的商机，届时将有3000多种玻璃行业的新设备新材料亮相，同期将举行华南玻璃技术专家座谈会。', '3', '<p>\r\n	<span style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;line-height:24px;\">&nbsp;&nbsp; 近年来，受经济下行影响，房地产市场明显降温，新开工项目相对减少，玻璃的需求受到一定影响，许多玻璃深加工企业订单减少、收款困难，市场上“价格战”盛行，利润大幅流失，沦为“大自然（砂子）的搬运工”。玻璃行业面临诸多困境，又加上人工成本上涨、环保等各方面压力，部分玻璃企业处于水深火热之中，玻璃深加工行业正进入隆冬，生意越来越难做了，过去“单打独斗”的商业模式已难以为继，转型升级迫在眉睫！本次展会作为华南年度唯一一个最专业的玻璃工业展览会，华南玻璃行业重要的展览展示交易平台，将为技术领先的国际化公司和专业化为特色的中小企业带来更多的商机，届时将有3000多种玻璃行业的新设备新材料亮相，同期将举行华南玻璃技术专家座谈会，以此论坛为契机，促进行业交流与合作，只有谋求业内合作、强强联合、取长补短、抱团取暖、共同应对，才能度过这个漫长的冬天，才能保持行业健康持续的发展！</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;line-height:24px;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 海外买家行程路线都已经安排妥当</span></strong>\r\n</p>\r\n<p>\r\n	<strong><br />\r\n</strong>\r\n</p>\r\n<span style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;line-height:24px;\">&nbsp;&nbsp;&nbsp; 除了买家数量的持续增多之外，展会规模也在不断扩大，截止8月31日展会规模首破10000平方米，巴西、意大利、德国、英国、美国、澳大利亚等多国驻广州领事机构表示会有国家参观访问团，机票行程都已经安排妥当。经主办单位的海外买家部统计，目前海外买家数量已经突破4308人次，</span><br />\r\n<span style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;line-height:24px;\">&nbsp;&nbsp; 广州国际玻璃工业展作为华南玻璃行业重要的展览展示交易平台，将为技术领先的国际化公司和专业化为特色的中小企业带来更多的商机，届时特纳江玻公司将展出公司最新一代产品“纳米硅防火玻璃”，此种玻璃可以防火隔热达4小时以上。同时在建筑安全住宅类玻璃领导品牌的新华强公司将携带公司专利核心产品亮相展会，新华强玻璃科技有限公司主要以生产夹层玻璃、彩釉玻璃、地铁玻璃、镀膜玻璃、中空玻璃、防火玻璃、平/弯钢化玻璃、家私产品等，公司产品已申请行业多项专利，此次展会是新华强公司首次携带核心专利产品亮相，展会期间将有新加坡、韩国、日本、意大利等13家国际技术领先的公司亮相，将为更多的中国玻璃制造企业带来商机。</span>', '20151013/346e047d7a17434699b70f502310c7ed.jpg', '', '玻璃展会', '华南年度唯一专业玻璃工业展览会促进行业交流与合作', 'yy.tanhualong', null, null);
INSERT INTO `article_base` VALUES ('12', '0', '1444701670445', 'yy.tanhualong', 'yy.tanhualong', '1444701670445', null, '2016年6月12日至14日第十六届成都建筑及装饰材料博览会（简称“成都建博会”）将在成都世纪城新会展中心举行，将重点推动西部地区二三线城市建材市场发展。据了解本届成都建博会将历时3天，设立6个展馆，展出面积达8万平方米，预计吸引海内外专家及专业观众6万人次左右。', '3', '<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	<span class=\"Apple-converted-space\">&nbsp;&nbsp;&nbsp; </span>2016年6月12日至14日第十六届成都建筑及装饰材料博览会（简称“成都建博会”）将在成都世纪城新会展中心举行，将重点推动西部地区二三线城市建材市场发展。据了解本届成都建博会将历时3天，设立6个展馆，展出面积达8万平方米，预计吸引海内外专家及专业观众6万人次左右。\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	新轮西部大开发 建材市场商机大\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	&nbsp;&nbsp;&nbsp; 随着经济的发展，2014年全国消费额达到26万亿元，城镇消费占据其中的22.1万亿元，城镇市场消费比例已经达到85%。其中二三线城镇市场成为消费中成长最快的区域。随着城镇化步伐加快，所以未来建材行业必然走向二三线市场发展。<br />\r\n在新一轮西部大开发进程中，成渝经济区已成为全国重要的经济增长极，如成都市“天府新区”（规划总面积1578平方公里）的开工建设、“北改项目”（规划总面积195平方公里）的深入实施，都将为建筑装饰材料带来巨大商机。<br />\r\n历经15届发展的成都建博会目前已成为是中西部地区举办时间最长、规模最大的建筑、装饰材料B2B商贸展，被誉为“国内建筑及装饰材料供应商开拓并巩固中西部市场的必选展会平台”。\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	深耕西部二三线城市 建博会力拓新市场\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	&nbsp;&nbsp;&nbsp; 作为房地产市场泡沫较少的西部二三线城市，市场发展相较稳定，建材市场的需求不断提升。据主办方介绍，2015成都建博会重点在来自云贵渝川近90家二级市县的专业市场的建材市场、装饰城、产业园区、8358家商铺组团参加。<br />\r\n&nbsp;&nbsp; &nbsp;根据2015年成都建博会现场观众统计数据显示，36%观众的来自云贵川二级市县，包括：德阳、绵阳、南充、乐山等地。在此基础上，可预计2016年成都建博会二级市县观众人数必创新高。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;成都市房地产开发企业协会 高耀华秘书长指出“近年来二三线城市的房地产市场泡沫较少，市场发展也相对稳定。随之带来的是人们对家居建材的需求增加。建材行业在未来的3—5年内，必然向二三线城市，中小型城市深化发展。建材企业想要抓住行业机遇占领二三线市场份额，成都建博会是一个很好平台。”\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	八万平展台空间 新技术展示建材新可能&nbsp;\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	&nbsp;&nbsp; &nbsp;2016成都建博会展期3天，展出面积超过8万平方米，共设六个主题展馆：4号馆/石材及设备馆；5号馆/建筑科技及智能家居馆；6号馆/门窗馆；7号馆/整体家居馆；8号馆/装饰材料综合馆；9号馆/暖通科技馆。<br />\r\n展出范围涵盖了石材产品及设备、保温防水、建筑门窗幕墙及其他建筑材料、装饰门窗及五金、墙纸布艺及墙面装饰材料、智能家居、灯饰照明、整体家居<a href=\"http://www.syjiancai.com/product/6403.asp\" target=\"_blank\">衣柜</a>、橱柜及电器、卫浴陶瓷、暖通空调、净水设备、空气净化等建材装饰行业最新科技技术应用、最新产品和最新工艺。<br />\r\n&nbsp;&nbsp;&nbsp; 商家们届时将通过产品展示、模拟演示、视频播放、亲身体验等方式全面系统地展示 “绿色、环保、节能、智能、高性能”的新产品和新技术将在人们生产及生活领域的应用，将会大大地改变日常生产及生活中高耗能、重污染及威胁人们健康等现象。\r\n</p>', '20151013/480f9442f76e43468c04af8a0d67133b.png', '', '成都建博会', '2016第十六届成都建博会启动 致力拓展西部二三级市场', 'yy.tanhualong', null, null);
INSERT INTO `article_base` VALUES ('13', '0', '1444701974434', 'yy.tanhualong', 'yy.tanhualong', '1444701974434', null, '       以“引领低碳设计，推动绿色建筑”为主题的2015第13届上海国际保温材料与节能技术展览会（TIM Expo Shanghai）将于11月4-6日在上海新国际博览中心隆重开幕。', '3', '<span style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;line-height:24px;\"></span><span style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;line-height:24px;\">\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	以“引领低碳设计，推动绿色建筑”为主题的2015第13届上海国际<a href=\"http://www.syjiancai.com/product/2800.asp\" target=\"_blank\">保温材料</a>与节能技术展览会（TIM Expo Shanghai）将于11月4-6日在上海新国际博览中心隆重开幕。本次盛会由中国建筑节能协会、中国绝热节能材料协会、上海市绿色建筑协会、中国房地产总工之家和中国国际贸易促进委员会上海浦东分会联合主办，上海展业展览有限公司承办。同期举办2015上海国际绿色建筑与节能展览会( GBC2015)，作为中国领先的可持续建筑技术博览会, 得到上海市政府、人大、建管委、建科院、房科院、中国勘察设计行业协会、上海勘察设计行业协会等部门的大力支持。\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	&nbsp;&nbsp;&nbsp; 作为亚洲保温行业的风向标展览，展会将展示最前沿的建筑节能<a href=\"http://www.syjiancai.com/product/2800.asp\" target=\"_blank\">保温材料</a>和技术，为我国现阶段大力推行的建筑节能改造、建筑工业化、装配式住宅、被动式超低能耗建筑及绿色建筑发展提供强力支持。同期GBC以“建筑工业化和建筑信息化”为核心内容, 展示国内外领先的工业化建筑设计理念、创新结构体系,重点突出建筑信息模型（BIM）等信息技术在工程设计、施工和运行维护全过程的应用。展会同期还包括绿色建筑系统与节能技术、城市规划与工程设计、绿色建材与技术、绿色停车设备与技术等板块，全面展现绿色建筑完整生命周期，展示面积45,000 平米, 预计参展企业 600 家，专业观众达30 ,000 人次。\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	展会精彩看点：<br />\r\n<span><strong>推广绿色建材，推进建筑行业可持续发展</strong><br />\r\n</span>保温及外墙装饰馆将展示国际前沿的岩棉、玻璃棉、保温装饰一体板、真空绝热板、真石漆、薄板等保温及新型外墙装饰材料和建筑节能技术。为企业提供专业的贸易展示平台，连接房地产开发商、建筑师、工程师、规划设计院、工程咨询、建设承包商、结构设计师、业主方、建设主管部门等重要专业人士，打造建筑节能饕餮盛宴，为绿色建筑和建筑节能提供专业服务，树立国际化的一站式采购平台。同期还将召开2015国际全生命周期绿色建筑与<a href=\"http://www.syjiancai.com/product/2800.asp\" target=\"_blank\">保温材料</a>新技术高峰论坛，2015上海国际彩钢夹芯板保温与防火技术研讨会等，欢迎行业人士参与。<br />\r\n<span><strong>建筑信息数据流自动化---建筑工业化峰会大咖云集</strong></span><br />\r\n建筑工业化作为建筑产业现代化发展的重要目标，承载着建筑节能、产业转型的使命。本届建筑工业化展区，展出规模较去年成倍增长，展品范围贯穿建筑工业化整个生命周期的上下游产业链。同期将举行欧洲混凝土行业最具影响力的年度活动之一上海国际建筑工业化峰会（BetonTage）。本届峰会以“建筑信息数据流自动化”为主题，将深度剖析国内外建筑工业化行业现状与趋势，30余位全球行业大咖将倾情分享建筑工业化发展动向、创新成果及实践经验。<br />\r\n<strong><span>建筑信息化（BIM），贯通建筑企业生命周期解决方案</span></strong><br />\r\nBIM技术已成为建筑业实现可持续发展和工业化的重要工具和手段。将重点展示建筑工程设计与规划、建筑信息化、建筑节能咨询服务、城市立体绿化、节能门窗、暖通空调、日光照明、幕墙系统、智能停车设备与技术、涂料以及建筑光伏一体化板块，以BIM为“主动脉”，贯穿规划、设计、施工、材料、运维等部分，将绿色建筑的全过程联结为完整的一体化产业链。同济设计研究院将携3D机器人空间打印技术，现场展示建筑模型3D打印过程。展会同期还将召开上海国际建筑信息化（BIM)与实践高峰论坛，汇聚BIM专家学者、业主方、设计师、总包单位等相关人士，针对热点话题，结合实际案例，深挖BIM发展动向。迪斯尼将亮相本届高峰论坛，现场分享建筑信息模型（BIM）技术在上海迪士尼奇幻童话城堡建设过程中的运用。\r\n</p>\r\n<p style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	TIM Expo 2015将于11月04-06日在上海新国际博览中心举行，为方便观众参观和及时获取重要信息，主办方将提供展会新闻、展商访谈、参观指南等，所有资讯皆可通过展会官网（www.timexpochina.com）及官方微信（TIMExpoShanghai）获取，快捷方便。期待您的莅临！\r\n</p>\r\n<br />\r\n</span>', '20151013/aa15c1bfc28243a2a589fc53bd718f7c.png', '', '绿色建筑', '中国先进建筑技术第一展 • 引领绿色建筑发展潮流', 'yy.tanhualong', null, null);
INSERT INTO `article_base` VALUES ('14', '0', '1444705584897', 'yy.tanhualong', 'yy.tanhualong', '1444705584897', null, '由香港新展国际展览有限公司联合广州瑞鸿展览服务有限公司联手打造的2015广州国际玻璃工业技术展览会将于9月23-25日在琶洲保利展馆举行，本次展会将有新加坡、韩国、日本、意大利等13家国际技术领先的公司亮相，为中国玻璃制造企业带来更多商机。', '3', '<span style=\"color:#000000;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal;line-height:24px;\">&nbsp;&nbsp;&nbsp; 近年来，受经济下行影响，房地产市场明显降温，新开工项目相对减少，玻璃的需求受到一定影响，许多玻璃深加工企业订单减少、收款困难，市场上“价格战”盛行，利润大幅流失，沦为“大自然（砂子）的搬运工”。玻璃行业面临诸多困境，又加上人工成本上涨、环保等各方面压力，部分玻璃企业处于水深火热之中，玻璃深加工行业正进入隆冬，生意越来越难做了，过去“单打独斗”的商业模式已难以为继，转型升级迫在眉睫！本次展会作为华南年度唯一一个最专业的玻璃工业展览会，华南玻璃行业重要的展览展示交易平台，将为技术领先的国际化公司和专业化为特色的中小企业带来更多的商机，届时将有3000多种玻璃行业的新设备新材料亮相，同期将举行华南玻璃技术专家座谈会，以此论坛为契机，促进行业交流与合作，只有谋求业内合作、强强联合、取长补短、抱团取暖、共同应对，才能度过这个漫长的冬天，才能保持行业健康持续的发展！</span>', '20151013/887ebad33ca04f89b27bfa23df053cf1.jpg', '', '玻璃展会', '13家国际技术领先公司亮相广州玻璃展会带来更多商机', 'yy.tanhualong', null, null);
INSERT INTO `article_base` VALUES ('15', '0', '1444705850894', 'yy.tanhualong', 'yy.tanhualong', '1444705850894', null, '9月21日上午，为期5天的第十一届沈阳法库国际陶瓷博览会在辽宁法库经济开发区盛大开幕，沈阳市副市长祁鸣、市政府副秘书长曲向军、沈阳市服务业委员会副主任任凤云、法库县代县长王欢苗等政府相关负责人出席会议，辽宁法库陶瓷协会会长李利民、各地协会代表及业内人士出席大会。', '3', '<p style=\"font-size:16px;font-style:normal;font-weight:normal;text-align:start;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;text-indent:37px;\">\r\n	<span><span style=\"font-size:19px;font-family:宋体;\">9月21日上午，为期5天的第十一届沈阳法库国际陶瓷博览会在辽宁法库经济开发区盛大开幕，沈阳市副市长祁鸣、市政府副秘书长曲向军、沈阳市服务业委员会副主任任凤云、法库县代县长王欢苗等政府相关负责人出席会议，辽宁法库陶瓷协会会长李利民、各地协会代表及业内人士出席大会。</span><span style=\"font-size:19px;font-family:宋体;\">辽宁省家具协会会长祖树武先生、副会长金亚凡女士应邀出席开幕式。</span></span>\r\n</p>\r\n<p style=\"font-size:16px;font-style:normal;font-weight:normal;text-align:start;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;text-indent:37px;\">\r\n	<span style=\"font-size:19px;font-family:宋体;\"><span>本届展会以“市场与创新”为主题。规模宏大，活动精彩纷呈是本届展会的一大亮色，展会共设四大展区，共55家企业参展，参展范围包括建筑陶瓷产品展销，艺术陶瓷、日用陶瓷产品展销，陶瓷机械及配套产品展销。主体活动包括中国瓷谷新材料科技企业孵化器揭牌仪式、辽宁法库新材料产业技术创新大会、2015中国陶瓷砖行业高峰论坛、“向泥土致敬”主题讲座、第七届陶瓷砖生产技术论坛、第八届中国建陶产业进出口高峰论坛、中国瓷谷·阿里巴巴渠道商战略合作签约仪式、法库县陶瓷旅游纪念品设计大赛，东北地区陶瓷书画交流笔会等活动。</span></span>\r\n</p>\r\n<p style=\"font-size:16px;font-style:normal;font-weight:normal;text-indent:0px;color:#3E3E3E;text-align:center;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;\">\r\n	<br />\r\n<span>&nbsp;</span>\r\n</p>\r\n<p style=\"font-size:16px;font-style:normal;font-weight:normal;text-align:start;color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;background-color:#FFFFFF;text-indent:37px;\">\r\n	<span style=\"font-size:19px;font-family:宋体;\"><span>开幕式结束后，省家协会长祖树武、副会长金亚凡在开宝庆福集团副总裁林茂的陪同下考察了浩松陶瓷有限公司，参观了生产线和新产品，与企业代表进行了深入交流，探讨陶瓷行业发展过程中的机遇和挑战、资源整合、拓展渠道等关心的问题，交谈在一片友好、和谐、欢乐的气氛中结束。</span></span>\r\n</p>', '20151013/f04b706fd1254f4682d6789384f3789b.png', '', '陶瓷展会 沈阳展会', '2015年第十一届沈阳法库国际陶瓷博览会开幕', 'yy.tanhualong', null, null);
INSERT INTO `article_base` VALUES ('16', '1', '1444705960339', 'yy.tanhualong', 'yy.tanhualong', '1444705960339', null, '“向泥土致敬”——2015沈阳·景德镇高端瓷器展暨陶瓷文化博览会,9月19日在素有“东北第一市”之称的和平区北市场的沈阳宏亿皇寺艺术馆盛大开幕,来自景德镇市和沈阳市的领导,部分省市老领导以及艺术大师出席了开幕式,来自景德镇的瓷乐队现场进行了精彩演出。', '3', '<p style=\"background-color:#f8f8f8;font-style:normal;text-indent:2em;font-family:宋体, simsun;color:#3d3c3c;font-size:14px;font-weight:normal;\">\r\n	“向泥土致敬”——2015沈阳·景德镇高端瓷器展暨陶瓷文化博览会,9月19日在素有“东北第一市”之称的和平区北市场的沈阳宏亿皇寺艺术馆盛大开幕,来自景德镇市和沈阳市的领导,部分省市老领导以及艺术大师出席了开幕式,来自景德镇的瓷乐队现场进行了精彩演出。\r\n</p>\r\n<p style=\"background-color:#f8f8f8;font-style:normal;text-indent:2em;font-family:宋体, simsun;color:#3d3c3c;font-size:14px;font-weight:normal;\">\r\n	此次高端瓷器展是景德镇市政府在外埠举办的首次高端瓷器展,展会规模5万平方米,展出1000余件大师级珍藏作品。展出时间从9月19日至10月18日,免费向市民开放。\r\n</p>\r\n<p style=\"background-color:#f8f8f8;font-style:normal;text-indent:2em;font-family:宋体, simsun;color:#3d3c3c;font-size:14px;font-weight:normal;\">\r\n	18位国家级、省级陶瓷大师在沈阳宏亿皇寺艺术馆瓷博会相应展区,对自己的作品向观众加以解读,以作者的角度解析瓷器创作及制造过程、工艺手法以及文化价值,让市民对展品有更深入的理解,通过大师的解读,触碰瓷器的灵魂,感悟瓷器的魅力。\r\n</p>\r\n<p style=\"background-color:#f8f8f8;font-style:normal;text-indent:2em;font-family:宋体, simsun;color:#3d3c3c;font-size:14px;font-weight:normal;\">\r\n	9月20日,景德镇国家级及省级大师还将在宏亿皇寺艺术馆二楼与辽沈知名书画大师及爱好者进行笔会,进行创作并交流。\r\n</p>\r\n<p style=\"background-color:#f8f8f8;font-style:normal;text-indent:2em;font-family:宋体, simsun;color:#3d3c3c;font-size:14px;font-weight:normal;\">\r\n	鲁园古玩城作为此次展会的分会场,也将展出具有一定艺术价值的古旧商品800余件。\r\n</p>\r\n<p style=\"background-color:#f8f8f8;font-style:normal;text-indent:2em;font-family:宋体, simsun;color:#3d3c3c;font-size:14px;font-weight:normal;\">\r\n	此次参展的1000余件大师级珍藏作品,将为北市场地区增添更加深厚的文化魅力。而作为展会承办地的和平区在陶瓷文化、古玩交易等方面同样有着深厚的历史,沈阳古玩城、北市古玩城、鲁园古玩城在东北地区久负盛名。此次举办的瓷博会,既是弘扬中华优秀文化、增进古玩艺术交流的有益实践,更是提升和平区文化内涵和文化魅力的有效途径,必将为和平区建设东北亚文化创意中心、平台经济中心注入新的生机和活力。\r\n</p>', '20151013/c540b35fbb964b41bbcbe8feeedefcb5.png', '', '景德镇陶瓷 ', '2015“向泥土致敬”——沈阳·景德镇高端瓷器展暨陶瓷文化博览会揭幕', 'yy.tanhualong', '', '');
INSERT INTO `article_base` VALUES ('17', '0', '1444706135462', 'yy.tanhualong', 'yy.tanhualong', '1444706135462', null, '2015国际绿色建筑建材（重庆）博览会将于11月20日至22日在重庆国际会展中心-南坪隆重开幕。展会得到重庆市城乡建设委员会、重庆市商务委员会大力支持，由上海市建筑材料行业协会、上海现代国际展览有限公司、重庆国际会议展览中心经营管理有限公司、德纳展览集团共同主办，同期还将举办第八届重庆绿色家博会。', '3', '<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	2015国际绿色建筑建材（重庆）博览会将于<span>11</span>月<span>20</span>日至<span>22</span>日在重庆国际会展中心<span>-</span>南坪隆重开幕。展会得到重庆市城乡建设委员会、重庆市商务委员会大力支持，由上海市建筑材料行业协会、上海现代国际展览有限公司、重庆国际会议展览中心经营管理有限公司、德纳展览集团共同主办，同期还将举办第八届重庆绿色家博会。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">重庆作为“一带一路”在西部地区的重要支点，与同为长江经济带战略要地的上海，首尾相连。依托上海绿色建博会十一届的成功经验，发挥重庆的地域优势。本次展览总面积达到</span><span style=\"line-height:1.5;\">15000</span><span style=\"line-height:1.5;\">平方米。自</span><span style=\"line-height:1.5;\">2015</span><span style=\"line-height:1.5;\">年</span><span style=\"line-height:1.5;\">7</span><span style=\"line-height:1.5;\">月招展以来，至今已有众多家企业踊跃报名参加，预订展位面积已达到</span><span style=\"line-height:1.5;\">8000</span><span style=\"line-height:1.5;\">平方米。其中包括</span><span style=\"line-height:1.5;\">2015</span><span style=\"line-height:1.5;\">国际绿色建筑建材（上海）博览会的部分优质品牌展商，率先一步对接西部市场发展，率先一步抢占行业商机。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">2015国际绿色建筑建材（上海）博览会顺应重庆建筑行业发展需求，首次将家装建材与建筑建材合并展出，针对建筑系统集成的各个环节，通过科学的整体设计，全方位体现“节约能源、节省资源、保护环境、以人为本”的基本理念，创造高效低耗、无废无污、健康舒适、生态平衡的建筑环境，提高建筑的功能、效率与舒适性水平。打造新技术、新产业、新模式、新业态，推动绿色建筑建材的推广和应用，推进绿色建筑建材事业发展，让绿色建筑建材走进寻常百姓家，为行业的发展输送一个个全新的“风向标”，打造一条绿色建筑产业创新链。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">展会内容包括：建筑节能及新型建材；绿色建筑智能舒适系统；建筑结构节能技术；绿色装饰建材与设计；绿色建筑、绿色工程示范成果、设计、技术服务展等。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">作为上海绿色建博会在西部地区的首展，组委会积极与重庆市政府相关部门、行业组织及房产企业沟通合作，得到了各方的大力支持。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">重庆渝开发股份有限公司（合作方之一的重庆国际会展中心的母公司）利用其在重庆房产市场的影响力和建筑领域号召力，必将聚集大量的专业观众和买家前来参观、采购。重庆市城乡建设委员也将邀请当地</span><span style=\"line-height:1.5;\">30</span><span style=\"line-height:1.5;\">几个区县的相关部门和单位共同参与。展会同时举办主题论坛和采购活动，形成销售、展览、合作、采购及技术交流的良好行业氛围。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">作为中国“全面提供绿色建筑整体解决方案”的国际建筑建材专业类品牌博览会，绿色建博会在上海经过近</span><span style=\"line-height:1.5;\">10</span><span style=\"line-height:1.5;\">多年的努力，是业内企业的重要市场推广渠道之一。全面覆盖商业、工业、民用建筑及室内装修，材料及系统配套等产品与技术。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">同时重庆作为我国最重要的工业城市之一，其经济增长的发展模式转变也在积极适应城市生态环境的需要。在顺应重庆绿色生态发展需求的大好形势和良好的绿色建筑建材的市场环境和政策环境的保障下，必将助力绿色建博会的绿色建筑建材推广和未来发展，以品牌效应带动区域影响力，全力将“绿色建博会”品牌概念延伸、拓展。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:21pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"line-height:1.5;\">欢迎具有前瞻眼光的企业及业内同仁参展、莅临参观考察，了解西部建筑市场的独特魅力，掌握西部市场建材应用脉络，西部的精彩里程我们共同开启！</span>\r\n</p>', '20151013/234b7c569b604bd486fe699ea3c152ca.png', '', '绿色建筑建材 重庆建博会', '从重庆出发打造西部绿建新生活 —— 2015国际绿色建筑建材（重庆）建博会开幕在即', 'yy.tanhualong', null, null);
INSERT INTO `article_base` VALUES ('18', '0', '1444706279826', 'yy.tanhualong', 'yy.tanhualong', '1444706279826', null, '围绕贯彻落实党的十八大精神，建设资源节约型、环境友好型社会，推动绿色建筑与建筑节能工作。由上海市建筑材料行业协会和东浩兰生集团上海现代国际展览有限公司共同主办的2015国际绿色建筑建材（上海）博览会（ESBUILD2015）（以下简称绿色建博会），将于6月30日至7月2日，在上海新国际博览中心拉开帷幕。', '3', '<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	围绕贯彻落实党的十八大精神，建设资源节约型、环境友好型社会，推动绿色建筑与建筑节能工作。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:1.5;\">由上海市建筑材料行业协会和东浩兰生集团上海现代国际展览有限公司共同主办的2015国际绿色建筑建材（上海）博览会（ESBUILD2015）（以下简称绿色建博会），将于6月30日至7月2日，在上海新国际博览中心拉开帷幕。作为中国唯一“全面提供绿色建筑整体解决方案”的国际建筑建材专业类贸易博览会，绿色建博会将承担起以上海为核心的华东地区，乃至全国范围内建筑建材市场的商贸合作、技术交流、项目对接等重要职能。聚焦反映对接国家生态文明建设的战略部署，全面推进绿色建筑技术产业化的最新阶段性成效。</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	2015绿色建博会定位“争做领跑者”，在同行业展会中，率先把绿色建筑系统集成作为从建筑节能到绿色建筑跨越式发展的发展体系和技术路线，推动建筑建材行业在提高城市环境质量，建设生态宜居城市中的重要作用。着重聚焦建筑围护结构技术、建筑结构节能技术、舒适系统节能技术、绿色装饰建材与设计、绿色建筑设备五大板块。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	2015国际绿色建筑建材（上海）博览会整体规模近150,000平方米，分12个展馆，将有来自30多个国家的近千家企业参展，预计吸引专业观众10万人次。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	绿色建博会将近万余种建筑材料、设备、工程企业的高新技术产品推向市场，设置30余场主题论坛，10多场专题技术讲座，50多场企业新技术、新产品发布会等一系列同期活动，使之成为政府、企业、社会共同探讨绿色建筑和建筑节能的盛会。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	与往年不同，主办方在本届博览会上特别开设国际品牌馆，诸多国外品牌争相以组团的形式参展。单个展团面积都达到500平方米左右。日本木材协会再次展示其在木结构方面的优势技术，韩国展团组织了大批采暖企业谋求开拓中国市场的合作，尼日利亚展团、加拿大木业也将强强联合进驻展会。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	展会邀请了上海及周边地区与建筑规划、建设、设计和工程、行业管理等相关的从业者，以及生产企业、研发机构、投资机构和建材贸易商。包括英、美、德、日、法、意、韩和奥地利、俄罗斯等多个国外商会组团前来参观。国内如万科、绿地、保利等大型房产开发企业及中国养老产业联盟等政府行业相关管理部门、科研院所、项目业主、采购商参与展会同期举办的各项活动。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:0cm;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<b>一、</b><b>建筑围护结构技术<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 由国内外知名企业欧文斯科宁、北新建材、聚氨酯协会、曹杨建筑粘合剂厂、振申、墙特、乐意、斯米克等“领航”建筑节能及新型建材企业，全面展示墙体保温装饰材料的最新技术与产品，<span>BOBO</span>板、欧诺创能等带来了最新的薄板技术；门窗幕墙及门业的企业参展数量也在上届基础上取得了重大性突破；防火阻燃材料展更是致力于打造专业节能产品与阻燃建材融合，中石化上海石化公司、江苏利思德带来了高端产品与技术，引领行业新趋势。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:0cm;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<b>二、</b><b>建筑结构节能技术<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 推广装配式建筑工业一体化，由上海建工集团等大型建筑企业提供的样板令人视野大开；<span>SRC</span>首次亮相让人们见证建筑个性化的“演绎”——数控技术使建筑不再受材料限制，其形式更加灵活多变。森工集团、阔森特、丰天等将娓娓道出大跨空间木结构在大型建筑方面的“故事”；而精功，蓝岛，大象，北新建材推出的冷弯薄壁型钢，将叙述集成房屋多元化的 “传奇”；已连续七年参展的日本木材协会也将带来最新技术的木结构房屋。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 钢筋加工与预制构件企业联合建筑模板、脚手架及施工技术企业共同参展，尤其建筑铝模、塑模及脚手架产品将在展会上全新亮相。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:0cm;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<b>三、</b><b>舒适系统节能技术<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	推动采暖行业规范化施工，E2馆推出“服务用户 品质提升”活动展区，由上海市建筑材料行业协会将出资30万打造采暖行业质量服务保障体系，为消费者提供无忧的行业售后保障。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	品牌企业齐亮相，唯嘉<span>Veiga</span>、A.O.史密斯家庭采暖、威能、菲斯曼、新昂、天合智能、博韬、卉金、洁水、保利、雅克菲、绿羽、天力、耐齐、暖舒特、阔盛等行业知名企业悉数亮相E2采暖馆，展示行业风采。而松下、兰舍、森德、六星、正泰、达达等也都是室内通风、空气净化及智能节电行业的佼佼者。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 今年展会上，建筑给排水管道系统也将展示全新升级后的众多新型产品，环保型供热设备逐渐成为绿色建筑供热节能首选，格兰富、同方工业等众多行业知名品牌将展现建筑水环境的健康、稳定、创新的可持续发展。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	展会现场活动丰富，《采暖系统施工技术人员上岗证》培训、“服务用户 品质先行”——行业质量服务保障体系启动仪式暨新闻发布会、HET新采暖 新生活 新方向——上海采暖与建筑新能源行业产品及施工技术应用高峰论坛（PART-VI）等内容，精彩纷呈。&nbsp;&nbsp;\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:0cm;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<b><br />\r\n</b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:0cm;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<b>四、</b><b>绿色装饰建材与设计<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 传承<span>17</span>届的别墅配套设施展，已经成为我国高端建筑和别墅配套设备行业规模最大、影响力最广的专业展览会，众多知名企业及历届优秀展商如艾默生钧康、中节能、澳贝尔、诺亚居、东南电梯、<span>CIBES</span>、德门迅、兴联集团、高尊、尊堡等齐聚一堂，展示硅藻泥、别墅太阳能、智能家居、舒适系统、别墅电梯、别墅高尔夫等高端建筑装饰建材产品与技术。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 城市微农业，绿色进万家，都市农业、屋顶绿化、垂直绿化成为绿色城市的一个新的闪光点，国际企业索普瑞玛，国内一线品牌海纳尔、中卉、绿创意等展示的新技术、新产品将让我们的建筑有更多绿色解决方案。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	此次展会的户外家具园林景观展也备受瞩目，户外花园家具及花园休闲用品、户外地板、景观防腐木、塑木制品、园林绿化企业汇聚一堂，安徽宏宇、厦门宝振、徐州天韵等众多优秀品牌为配套大型中高端房地产企业采购户外家具园林景观产品提供了一个最佳平台。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:0cm;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<b>五、</b><b>绿色建筑设备<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	随着首届“上海国际陶瓷玻璃装饰数字喷印技术设备展”的同期举办，标志着喷墨印刷技术应用正式进入建筑陶瓷、玻璃、壁画、装饰木板、门窗、塑胶板等领域。这种技术具有节省制造费用、设计方案更新更快、节省调机时间、减少库存量、缩短制造周期、图案更加清晰、立体感更强等优势。这将是最终实现建筑装饰市场资源化、低碳化、数字化、个性化、功能化的必由之路。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\" align=\"left\">\r\n	钢筋加工与预制构件机械设备展区将与集成建筑、轻钢房屋及建筑钢构展同台亮。上海建工机施集团、上海城建<span>“</span>绿智汇<span>”</span>、无锡华夏、上海泓尔、苏州嘉鹏<span>MEP</span>、三林混凝土、福建神马、福建天马、天津建科、无锡新夏等企业将携最新的装配式建筑施工技术、预制构件产品、智能化高效能钢筋加工设备等亮相<span>W1</span>馆和<span>W2</span>馆。<span><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"Apple-converted-space\">&nbsp;</span></span>钢筋加工与预制构件施工产业现代化发展论坛研讨活动邀请政府相关部门、行业专家、施工单位、钢筋及钢筋构件的生产、贸易、加工企业等，围绕上海地区绿色建筑行动计划、建筑产业化发展政策解读、建设工程钢筋行业质量监管、我国工业化建筑发展道路及趋势思考、装配式建筑施工技术应用以及钢筋加工工业化、现代化发展，先进的钢筋加工与预制构建机械设备的最新发展及产品介绍进行，呼吁行业提高钢筋加工产品质量，提升建筑施工产业现代化发展，共同探索创造绿色、健康、环保的现代产业化发展之路。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:0cm;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<b>六、</b><b>行业发展及高峰论坛<span></span></b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 本届展会举办的系列论坛，同样涵盖了建筑围护结构技术、建筑结构节能技术、舒适系统节能技术、绿色装饰建材与设计、绿色建筑设备等绿色建筑发展方向。论坛分为主题发展及高峰论坛、技术讲座、参展企业产品发布及技术交流会三种形式展开。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 30多场主题发展及高峰论坛涉及安全质量创新墙体节能保温可持续发展论坛，生态·节能·景观建筑绿化新作为，创意城市创新动力创意<span>VS</span>产品发展论坛，绿色环保生态蜠化胶粘剂与瓷砖产业发展高峰论坛，智领先机 追求卓越钢筋加工与预制构件施工产业现代化发展论坛，采暖与建筑新能源行业产品及施工技术应用高峰论坛等。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 10多场技术讲座为今年展会一大亮点，为更好的促进展会现场的建材新技术的推广，吸引行业的关注目光，本次展会组织了一系列的相关行业讲座：建筑绿化技术培训，采暖施工人员上岗证培训，绿色建筑材料在现有建筑中的应用，涉及超高层建筑、绿色医院、博物馆、酒店等新技术的实际应用，上海地板行业铺装技能大赛，消能减震，既有建筑加固改造等。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:2em;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp; 50多场企业新产品自主推介会在上届满载而归的基础上，今年更是获得了更多企业的全情参与。从薄膜发电、智能光源、温控光能纳米膜到<span>3D</span>虚拟现实、<span>3D</span>打印与家装、大型<span>GRC</span>装配式建筑应用。汉能、卓达等一大批绿色建筑领域的优秀企业都将最新的产品推介放在展会现场举办。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;text-indent:28pt;color:#515151;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	论坛及产品展示和发布的重点意义不仅在于通过对各自热点交流分析，为行业发展输送一个个“风向标”，更在于提示行业的出路不只是止于我们单个企业的转型，关键是能否形成行业健康发展的系统专业引导平台及其体系。配套组织的工程项目现场对接会也同样备受瞩目。\r\n</p>', '20151013/1269ed6e201d47359197d5ef440f2d4c.png', '', '绿色建博会', '整合资源 创新转型 升级平台', 'yy.tanhualong', null, null);
INSERT INTO `article_base` VALUES ('19', '0', '1444706653752', 'yy.tanhualong', 'yy.tanhualong', '1444706653752', null, '       10月10日上午，第三届中国绿色建材产业合作论坛暨2015宿迁国际绿色建材展览会于宿迁市国际会展中心隆重开幕。本届论坛暨展会由宿迁市人民政府和中国建材报社主办，宿迁市宿城区人民政府和南京斯图加特联合展览有限公司承办。展出面积共5000平米，共吸引国内外展商近150家。', '3', '<p style=\"text-indent:2em;font-size:14px;color:#2A2A2A;font-family:SimSun, Arial;font-style:normal;font-weight:normal;text-align:start;\">\r\n	10月10日上午，第三届中国绿色建材产业合作论坛暨2015宿迁国际绿色建材展览会于宿迁市国际会展中心隆重开幕。本届论坛暨展会由宿迁市人民政府和中国建材报社主办，宿迁市宿城区人民政府和南京斯图加特联合展览有限公司承办。展出面积共5000平米，共吸引国内外展商近150家。\r\n</p>\r\n<p style=\"text-indent:2em;font-size:14px;color:#2A2A2A;font-family:SimSun, Arial;font-style:normal;font-weight:normal;text-align:start;\">\r\n	国务院参事蒋明麟、工信部原材料司司长周长益、青海省西宁市副市长许国城、宿迁市市委书记魏国强、宿迁市市长王天琦、宿迁市人民政府常务副市长吕德明、经济日报内参部主任孙世芳、《中国建材报》社社长杨军、德国加特展览公司总裁穆勒等领导出席了开幕式。\r\n</p>\r\n<p style=\"text-indent:2em;font-size:14px;color:#2A2A2A;font-family:SimSun, Arial;font-style:normal;font-weight:normal;text-align:start;\">\r\n	宿迁市市委书记魏国强发表致辞：“绿色发展既是当今时代的发展潮流，更是区域经济发展的不懈追求。绿色建材产业作为绿色发展的重要切入点和发力点，市场潜力巨大、发展前景广阔，代表着建筑材料未来的发展方向。”并预祝本次展会取得圆满成功。德国斯图加特展览公司高级副总裁班穆勒在开幕式致辞：“通过展会与洽谈会、论坛的科学组织，宿迁成功的将行业前沿的技术探讨与国内外绿色建筑产品、技术的集中展示结合在一起，系统打造了一个服务于发展本地建材产业的全新平台。”\r\n</p>\r\n<p style=\"text-indent:2em;font-size:14px;color:#2A2A2A;font-family:SimSun, Arial;font-style:normal;font-weight:normal;text-align:start;\">\r\n	此外，江苏省建筑金属结构协会、江苏省混凝土协会等相关负责人以及来自欧洲等多个国家和地区的企业负责人也参加了本次开幕式。开幕式后召开的第三届中国绿色建材产业合作论坛是汇聚业内精英的第一个以促进绿色建材产业链交流与互动为宗旨的创新平台，为推动宿迁市绿色建材产业发展，形成产学研一体化、全方位、多渠道的绿色建材事业做出了贡献。\r\n</p>\r\n<p style=\"text-indent:2em;font-size:14px;color:#2A2A2A;font-family:SimSun, Arial;font-style:normal;font-weight:normal;text-align:start;\">\r\n	工信部原材料司周长益司长在会上对《促进绿色建材生产和应用行动方案》做了解读，宿迁市市长王天琦作主题报告，“合作论坛成功举办的背后，正式源于我们对绿色梦想的坚守，把美好愿景付诸于点滴实践，用行动力量推动了绿色建材发展壮大。”中国建材工业经济研究会会长刘长发在论坛上发表《绿色建材发展的前景趋势与路径》的演讲。FTA首席设计官、合伙人 Thomas Fritzsche(托马斯&amp;middot;福利智)发表了”三维一体的绿色园区”的演讲。金螳螂景观设计院院长、创城爱康建筑科技有限公司董事长徐军也在论坛上发言。\r\n</p>\r\n<p style=\"text-indent:2em;font-size:14px;color:#2A2A2A;font-family:SimSun, Arial;font-style:normal;font-weight:normal;text-align:start;\">\r\n	论坛如火如荼进行的同时，2015宿迁国际绿色建材展览会也如约而至。据了解，本届展会划分为国际展区、宿迁建材展区、暖通展区、绿色建材、家居展区五大主题展区。展出内容包含了建筑工业化及家居建材、幕墙及配件、吊顶、隔断、整体卫浴、暖通空调、建筑保温材料、太阳能光伏、地源热泵等绿色建筑、建材等相关产品及服务。\r\n</p>\r\n<p style=\"text-indent:2em;font-size:14px;color:#2A2A2A;font-family:SimSun, Arial;font-style:normal;font-weight:normal;text-align:start;\">\r\n	论坛暨展会的开展是为企业提供贸易匹配、现场专项洽谈、项目推荐跟踪等专业平台。同时论坛与展会相结合的互动模式，高效展示宿迁投资环境、产业招商政策，为江苏宿迁绿色建材市场打造了优质的交流合作平台，为“树立四海意识、融入一带一路、发展绿色产业、创建生态文明”贡献了力量。\r\n</p>\r\n<br />', '20151013/3946daf20491467ca310627146b2e0ba.png', '', '绿色建材', '第三届中国绿色建材产业合作论坛暨2015宿迁国际绿色建材展会开幕', 'yy.tanhualong', null, null);
INSERT INTO `article_base` VALUES ('20', '4', '1444706925808', 'yy.tanhualong', 'yy.tanhualong', '1444706925808', null, '      由安徽省建筑业协会、安徽省房地产业协会、合肥市建筑节能科技协会、安徽省贸促会、安徽好博塔苏斯展览公司等15家机构联合举办的2015中国（安徽）国际绿色建筑建材博览会于2015年10月15日-17日在合肥滨湖国际会展中心隆重召开。', '3', '<div class=\"g-content-c\" style=\"color:#333333;font-family:simsun;font-size:14px;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#333333;font-family:新宋体, 宋体;font-size:14px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;background-color:#FFFFFF;\">\r\n		由安徽省建筑业协会、安徽省房地产业协会、合肥市建筑节能科技协会、安徽省贸促会、安徽好博塔苏斯展览公司等15家机构联合举办的2015中国（安徽）国际绿色建筑建材博览会于2015年10月15日-17日在合肥滨湖国际会展中心隆重召开。\r\n	</p>\r\n	<p style=\"color:#333333;font-family:新宋体, 宋体;font-size:14px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;background-color:#FFFFFF;\">\r\n		目前，安徽合肥、滁州、蚌埠、芜湖、六安、池州等地市政府出台明确推进建筑产业化的新措施，宇辉集团、安徽望湖建筑产业化、德州海天等企业迅速入驻，带来“PC构件、装配或整体式剪力墙” 等各类建筑产业化展品，这些产品将极大程度上减少建筑垃圾的产生、降低建筑噪音的干扰、PM2.5等有害气体及粉尘的排放。上海君轩节能、马鞍山宏达、蒙城义鼎、山东天元、安徽欧福、合肥志恒等一批企业则携“保温材料、保温一体板”隆重亮相。蒙娜丽莎集团“陶瓷薄板”、上海尚瑾“合成大理石板”也是久负盛名，携新品前来；青岛安日达的“门窗”系列将在现场刮起浓郁的欧洲风，带你体验别样的异族风情。\r\n	</p>\r\n	<p style=\"color:#333333;font-family:新宋体, 宋体;font-size:14px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;background-color:#FFFFFF;\">\r\n		值得期待的是建筑防水专题展区，业内大腕云集，深圳卓宝、北京东方雨虹、泰州奥佳、潍坊正泰防水、合肥中通、安徽立德等，大到屋面防水工程，小到卫生间防水补漏维修翻新以及环氧地坪施工技术，将呈现一站式建筑防水解决方案。\r\n	</p>\r\n	<p style=\"color:#333333;font-family:新宋体, 宋体;font-size:14px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;background-color:#FFFFFF;\">\r\n		安徽顺达新能源还将带来太阳能热水器新产品，节能环保，适合于大型企事业单位使用，随州永扬石业、RICA-巴基斯坦工艺石等则绽放石材魅力，天津旭洋、德国富亿“地暖材料”正当适用之际……。目前入驻的这些展品某种程度代表新型建材潮流，此外，还有节能板材、玻璃钢化粪池、塑木科技、围护栏、沙岩、钢结构、绿色照明、智能建筑等众多新型节能产品。\r\n	</p>\r\n	<p style=\"color:#333333;font-family:新宋体, 宋体;font-size:14px;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;background-color:#FFFFFF;\">\r\n		目前统计，本次建博会将展出1200个种类的建筑产品。据专家分析，由于绿色建筑已经上升到“国家战略”层面，2015年安徽省建筑业总额将达到7700亿，建筑产业化进程遍布皖地，让众多绿色建材企业对发展前景很是乐观，都积极争夺中部市场份额。而新产品新技术在专业建博会上的亮相，将直接起到“树品牌、建网络、测新品、促销售”四大会展功能效应。\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>\r\n<br class=\"Apple-interchange-newline\" />', '20151013/321732e263f54abb9576859961863512.png', '', '家居展销会', '安徽建博会金秋举办 引爆“绿色建材”商机', 'yy.tanhualong', '筑牛,安徽,建筑', '筑牛,安徽,建筑');

